from functools import lru_cache
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def backtrack(i, last_a, last_b):
            if i == len(nums):
                return 1  # Base case: valid pair found

            count = 0
            for a in range(nums[i] + 1): 
                b = nums[i] - a  
                if (i == 0 or last_a <= a) and (i == 0 or last_b >= b):
                    count += backtrack(i + 1, a, b)
                    count %= mod  
            return count

        return backtrack(0, 0, nums[0])
