class Solution {
public:
    static const int m=1e9+7;
    int dp[51][51][2001];
    int solve(int p1, int p2, int ind, vector<int>& nums){
        if(ind==nums.size()){
            return 1;
        }

        if(dp[p1][p2][ind]!=-1)
            return dp[p1][p2][ind];

        int ans=0;
        for(int i=0; i<=nums[ind]; ++i){
            if(i>=p1 && nums[ind]-i>=0 && nums[ind]-i<=p2){
                ans=(ans%m+solve(i, nums[ind]-i, ind+1, nums)%m)%m;
            }
        }
        return dp[p1][p2][ind]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        int ans=0;
        for(int i=0; i<=nums[0]; ++i){
            if(nums[0]-i>=0){
                ans=(ans%m+solve(i, nums[0]-i, 1, nums)%m)%m;
            }
        }
        return ans;
    }
};
