class Solution {
public:
    int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(51));
        for (int j=0; j<=nums[0]; ++j) dp[0][j] = 1;
        for (int i=1; i<n; ++i) for (int j=0; j<=nums[i]; ++j) {
            for (int k=0; k<=nums[i-1]; ++k) {
                if (j >= k && nums[i] - j <= nums[i-1] - k) {
                    dp[i][j] += dp[i-1][k];
                    dp[i][j] %= MOD;
                }
            }
        }
        int res = 0;
        for (int j=0; j<=50; ++j) {
            res += dp[n-1][j];
            res %= MOD;
        }
        return res;
    }
};
