class Solution {
public:
    const int M = 1e9 + 7;
    int countOfPairs(vector<int>& n) {
        int s = n.size();
        vector<vector<vector<int>>> d(s, vector<vector<int>>(51, vector<int>(51, 0)));
        for (int j=0;j<=n[0];j++) {
            int k =n[0]- j;
            d[0][j][k] = 1;
        }
        for (int i=1;i<s;i++) {
            for (int j = 0; j<= n[i];j++) {
                int k = n[i]-j;
                for (int pj =0;pj<=n[i-1]; ++pj) {
                    int pk=n[i-1]-pj;
                    if (pk>=0&&pj<=j&&pk>=k)
                        d[i][j][k]=(d[i][j][k]+d[i-1][pj][pk])%M;
                }
            }
        }
        int r = 0;
        for (int j=0;j<=n[s-1];j++) {
            int k=n[s-1]-j;
            r=(r+d[s-1][j][k]) % M;
        }
        return r;
    }
};
