class Solution {
public:
    int countOfPairs(vector<int>& nums) {
      unordered_map<long long, long long> dp;
        return solve(nums, 0, 0ll, 50, dp);
    }

private:
    long long hash(int i, long long mn, long long mx) const {
        const long long base = 100000; 
        return i * base * base + mn * base + mx;
    }

    long long solve(vector<int>& nums, int i, long long mn, long long mx, unordered_map<long long, long long> &dp) {
        if (i >= nums.size()) return 1;

        long long key = hash(i, mn, mx);
        if (dp.find(key) != dp.end()) return dp[key];

        long long count = 0;
        for (int j = 0; j <= nums[i]; ++j) {
            if (j >= mn && nums[i] - j <= mx) {
                count = (count + solve(nums, i + 1, j, nums[i] - j, dp)) % 1000000007;
            }
        }

        dp[key] = count;
        return count;
    }
};
