#include <vector>
using namespace std;

const int MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n + 1, vector<int>(51, 0));

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                dp[i][j] = 0;
                for (int k = 0; k <= j; ++k) {
                    int arr2_val = nums[i] - j;
                    if (arr2_val <= nums[i - 1] - k && arr2_val >= 0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }

        int result = 0;
        for (int j = 0; j <= nums[n - 1]; ++j) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
};
