class Solution {
public:
    int mod = 1e9 + 7;
    int solve(int ind, int increase, int decrease, vector<int>& nums, vector<vector<vector<int>>>& dp) {
        if(ind == nums.size()) {
            return 1;
        }

        if(dp[ind][increase][decrease] != -1) return dp[ind][increase][decrease];

        int cnt = 0;

        for(int val = 0; val <= nums[ind]; val++) {
            int rem = nums[ind] - val;
            if(val < increase || rem > decrease) continue;
            cnt = (cnt + solve(ind+1, val, rem, nums, dp)%mod)%mod;
        }

        return dp[ind][increase][decrease] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>> (51, vector<int> (51, -1)));

        return solve(0, 0, 50, nums, dp);
    }
};
