import java.util.Scanner;

class Solution {
    public int countOfPairs(int[] nums) {
        final int MOD = 1000000007;
        int n = nums.length;
        int maxVal = 50;

        // Create a 3D DP table
        int[][][] dp = new int[n][maxVal + 1][maxVal + 1];

        // Initialize the DP table for the first element
        for (int j = 0; j <= maxVal; j++) {
            int k = nums[0] - j;
            if (k >= 0 && k <= maxVal) {
                dp[0][j][k] = 1;
            }
        }

        // Fill the DP table
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= maxVal; j++) {
                for (int k = 0; k <= maxVal; k++) {
                    if (dp[i - 1][j][k] > 0) {
                        for (int jNew = 0; jNew <= j; jNew++) {
                            int kNew = nums[i] - jNew;
                            if (kNew >= k && kNew <= maxVal) {
                                dp[i][jNew][kNew] = (dp[i][jNew][kNew] + dp[i - 1][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        }

        // Sum up all valid pairs for the last index
        int result = 0;
        for (int j = 0; j <= maxVal; j++) {
            for (int k = 0; k <= maxVal; k++) {
                result = (result + dp[n - 1][j][k]) % MOD;
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        Solution sol = new Solution();

        System.out.print("Enter the number of elements: ");
        int n = sc.nextInt();

        int[] nums = new int[n];
        System.out.print("Enter the elements: ");
        for (int i = 0; i < n; i++) {
            nums[i] = sc.nextInt();
        }

        System.out.println("The number of monotonic pairs is: " + sol.countOfPairs(nums));
    }
}
