/*  All Required define Pre-Processors and typedef Constants */
typedef long int int32;
typedef unsigned long int uint32;
typedef long long int int64;
typedef unsigned long long int  uint64;

class Solution {
public:
    ll solve(int ind, int ind1, int ind2, vector<int>& nums, vector<vector<vector<ll>>>& dp){
        if (ind >= nums.size()) return 1;  
        ll ans = 0;
        if (dp[ind][ind1][ind2] != -1)return dp[ind][ind1][ind2];

        for (int i = ind1; i <= nums[ind]; i++) {
            for (int j = ind2; j >= 0; j--) {
                if (i + j == nums[ind]) {
                    ans += solve(ind + 1, i, j, nums, dp);
                    ans = ans % MOD;
                }
            }
        }

        return dp[ind][ind1][ind2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>>dp(n,vector<vector<ll>>(51,vector<ll>(51,-1)));

        return solve(0,0,nums[0],nums,dp);
    }
};
