class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = (10**9) + 7
        @functools.cache
        def helper(i, x, y):
            if i != -1 and x + y != nums[i]:
                return 0
            if i == len(nums)-1 and x+y == nums[i]:
                return 1
            total = 0
            for nextX in range(x, 51):
                if nextX > nums[i+1]:
                    continue
                nextY = nums[i+1]-nextX
                if nextY > y:
                    continue
                total += helper(i+1, nextX, nextY) % MOD
            return total % MOD

        return helper(-1, 0, 50) % MOD
