class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int sizes = values.size();
        const long long MOD = 1e9 + 7;
        vector<vector<vector<long long>>> memo(sizes, vector<vector<long long>>(51, vector<long long>(51, -1)));

        function<long long(int, int, int)> solve = [&](int indexs, int val1, int val2) -> long long {
            if (indexs >= values.size()) return 1;
            long long result = 0;
            if (memo[indexs][val1][val2] != -1) return memo[indexs][val1][val2];
            for (int i = val1; i <= values[indexs]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == values[indexs]) {
                        result = (result + solve(indexs + 1, i, j)) % MOD;
                    }
                }
            }
            return memo[indexs][val1][val2] = result;
        };

        return solve(0, 0, values[0]);
    }
};
