class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long kMod = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(51, 0));

        for (int x = 0; x <= nums[0]; ++x) {
            dp[0][x] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int x = 0; x < 51; ++x) {
                if (x > nums[i])
                    continue;
                for (int z = 0; z <= x; ++z) {
                    if (nums[i-1] - z >= nums[i] - x) {
                        dp[i][x] = (dp[i][x] + dp[i - 1][z]) % kMod;
                    }
                }
            }
        }
        //return accumulate(dp.back().begin(), dp.back().end(), 0L);
        long long ans = 0;
        for (int i = 0; i < 51; ++i)
            ans = (ans + dp.back()[i])%kMod;

        return ans;
    }
};
