class Solution {
public:
    int dp[2005][55][55];
    int mod = (int)(1e9+7);
    int recur(int i,int pre1,int pre2,vector<int> &nums){
        if(i==nums.size()) return 1;

        int &d = dp[i][pre1+1][pre2];
        if(d!=-1) return d;

        int cnt = 0;
        for(int fir=0;fir<=nums[i];fir++){
            int sec = nums[i]-fir;
            if(fir>=pre1 and sec<=pre2){
                cnt += recur(i+1,fir,sec,nums);
                cnt %= mod;
            }
        }
        return d = cnt%mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> arr1(n+1,0),arr2(n+1,55);
        memset(dp,-1,sizeof(dp));
        return recur(0,-1,51,nums)%mod;
    }
};
