class Solution {

private:
    int dp[2000][52][52];
    int mod=1e9+7;
    int solve(int indx,int prev1,int prev2,int n,vector<int> &nums){

        if(indx>=n)
            return 1;

        if(dp[indx][prev1+1][prev2+1]!=-1)
            return dp[indx][prev1+1][prev2+1];

        int ans=0;
        for(int num=0;num<=nums[indx];num++){

            if((prev1==-1) || prev1>=num && prev2<=nums[indx]-num)
                ans=(ans+solve(indx+1,num,nums[indx]-num,n,nums))%mod;
        }
        return dp[indx][prev1+1][prev2+1]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {

        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,-1,-1,n,nums);
    }
};
