const mod = 1000000007;

function add(u: number, v: number) {
  return Number((BigInt(u) + BigInt(v)) % BigInt(mod));
}
function countOfPairs(nums: number[]): number {
  const n = nums.length;
  const max_a = 52;
  const dp: number[][] = new Array(n).fill(0).map(() => new Array(max_a + 1).fill(0));

  for (var i = 0; i <= nums[0]; i++) {
    dp[0][i] = 1;
  }
  for (var i = 1; i < n; i++) {
    for (var t2 = 0; t2 <= nums[i]; t2++) {
      for (var t1 = 0; t1 <= nums[i - 1]; t1++) {
        // arr1: tang dan
        if (t2 < t1) {
          continue;
        }

        // arr2: giam dan
        if (nums[i] - t2 > nums[i - 1] - t1) {
          continue;
        }
        dp[i][t2] = add(dp[i][t2], dp[i - 1][t1]);
      }
    }
  }
  var res = dp[n - 1].reduce((s, u) => (s = add(s, u)), 0);
  return res;
}
