class Solution
{
private:
    long long rec(int i, int la, int lb, vi &v, vector<vvi> &dp)
    {
        // deb(i);
        // deb2(la,lb);
        if (i == v.size())
            return 1;

        ll res = 0;
        if (la >= 0 && lb <= 50 && dp[i][la][lb] != -1)
            return dp[i][la][lb];
        fo(a, v[i] + 1)
        {
            int b = v[i] - a;
            if (a >= la && b <= lb)
            {
                res += rec(i + 1, a, b, v, dp);
                res %= mod;
            }
        }
        if(la>=0 && lb<=50)
            return dp[i][la][lb] = res;
        else
            return res;
    }

public:
    int countOfPairs(vector<int> &v)
    {
        vector<vvi> dp(v.size() + 1, vvi(55, vi(55, -1)));
        return rec(0, -1, 50, v, dp);
    }
};
