class Solution {
public:
    int mod = 1e9+7;
    int f(int i, int prea, int preb, vector<int> &a, vector<vector<vector<int>>> &dp){
        if(i>=a.size()) return 1;
        if(dp[i][prea][preb]!=-1) return dp[i][prea][preb];
        long long ans = 0;
        for(int ind=0;ind<=a[i];ind++){
            if(ind >= prea && (a[i] - ind) <= preb){
                ans = (ans + f(i+1, ind, a[i]-ind, a, dp)) % mod;
            }
        }
        return dp[i][prea][preb] = (int)ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));
        return f(0, 0, 51, nums, dp);
    }
};
