long long mod=1e9+7;
class Solution {
public:
    long long solve(int i,int p1,int p2,vector<int>&nums,vector<vector<vector<int>>>&dp){
        int n=nums.size();
        if(i>=n){
            return 1;
        }
        if(dp[i][p1][p2]!=-1) return dp[i][p1][p2];
        long long ans=0;
        for(int k=0;k<=nums[i];k++){
            int a=k;
            int b=nums[i]-k;
            if(a>=p1 && b<=p2){
                ans=ans+solve(i+1,a,b,nums,dp);
                ans%=mod;
            }
        }
        return dp[i][p1][p2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        long long ans=solve(0,0,50,nums,dp);
        ans%=mod;
        return ans;
    }
};
