class Solution {
public:
    int helper(int ind, int prev1, int prev2, vector<int> &nums, vector<vector<vector<int>>> &dp){
        if(ind >=nums.size()) return 1;
        if(dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2];
        int cnt =0;
        for(int i =prev1;i<=nums[ind];i++){
            int arr1 = i;
            int arr2 = nums[ind] - i;
            if(ind > 0 && arr1<prev1) continue;
            if(ind > 0 && arr2>prev2) continue;
            cnt= (cnt + (helper(ind+1, arr1, arr2, nums, dp))%1000000007) % 1000000007;
        }
        return dp[ind][prev1][prev2] = cnt % 1000000007;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size()+1, vector<vector<int>>(51, vector<int>(51,-1)));
        return helper(0,0, 0, nums, dp);         
    }
};
