/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    const modi = 1e9+7;
    let res = 0;
    const memo = new Map();

    const rec = (i,prev1,prev2) => {
        if(i===nums.length) return 1;

        const key = `${i}_${prev1}_${prev2}`;
        if (memo.has(key)) {
            return memo.get(key);
        }

        let count = 0;
        for (let p = prev1;p<=nums[i];p++) {
            let q = nums[i]-p;
            if (q<=prev2) {
                count=(count+rec(i+1,p,q)) % modi;
            }
        }

        memo.set(key,count);
        return count;
    }

    res = rec(0,0,Infinity);

    return res;
};
