class Solution {
public:
    const int mod = 1e9 + 7;
    int dp[2001][51][51];
    int solve(int i,int pa,int pb, vector<int> &nums){
        if(i >= nums.size()){
            return 1;
        }
        if(dp[i][pa][pb] != -1) return dp[i][pa][pb];

        long long res = 0;
        for(int x = pa;x <= 50;x++){
            int y = nums[i] - x;
            if(y <= pb && y>=0){
                res += solve(i+1, x, y,nums);
            }
        }

        return dp[i][pa][pb] = res%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 0, 50, nums);
    }
};
