class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> arr = nums;
        long long dp[2000][51];
        memset(dp,0,sizeof(dp));
        for(int j=0;j<=arr[n-1];j++){
            dp[n-1][j] = 1;
        }
        long long m = 1000000007;
        for(int i=n-2;i>=0;i--){
          for(int j=0;j<=arr[i];j++){
            for(int k=0;k<=arr[i+1];k++){
                if(j<=k && (abs(arr[i+1]-k)<=abs(arr[i]-j))){
                    dp[i][j]=(dp[i][j]%m + dp[i+1][k]%m)%m;
                }
             }
            }
        }

        long long ans = 0;
        for(int i=0;i<=arr[0];i++){
            ans = (ans%m + dp[0][i]%m)%m;
        }

        return ans;

    }
};
