class Solution {
public:
    int mod=1e9+7;
    int solve(int index, int prev1, int prev2, vector<int>& nums,
    vector<vector<vector<int>>>&dp) {
        if (index == nums.size()) return 1;
        if(dp[index][prev1][prev2]!=-1)return dp[index][prev1][prev2];
        int ans = 0;
        for (int j = 0; j <= nums[index]; j++) {
            if (j >= prev1 && nums[index] - j <= prev2) {
                ans += solve(index + 1, j, nums[index] - j, nums,dp);
                ans %= mod; 
            }
        }
        return dp[index][prev1][prev2]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(100,vector<int>(100,-1)));
        return solve(0, 0, 99, nums,dp);
    }
};
