class Solution {
public:
     int md=1e9+7;
    long long dfs(int last,int ind,int n,vector<vector<int>>&dpp,vector<int>&b)
    {
        if(ind>=n)
        {
            return 1;
        }
        if(dpp[ind][last]!=-1)
            return dpp[ind][last];
        long long res=0;
        for(int v=last;v<=b[ind];v++)
        {


            if(ind!=0)
            {

                int x=b[ind]-v;
                int y=b[ind-1]-last;
                if(y>=x)
                {
                   res=(res+dfs(v,ind+1,n,dpp,b))%md;  
                }
            }
            else
            {
                 res=(res+dfs(v,ind+1,n,dpp,b))%md;
            }
        }
        return dpp[ind][last]=res%md;
    }
    int countOfPairs(vector<int>& b) {
         int n=b.size();
        vector<vector<int>>dpp(n+10,vector<int>(60,-1));
        long long tata=dfs(0,0,n,dpp,b);
        return tata%md;
    }
};
