class Solution {
public:
    const int Mod = 1e9 + 7;
    int dp[2000][51][51], n;

int fun(int idx, int in, int de, vector <int> &a) {
        if (idx >= a.size()) return 1;
        int ans = 0;
        int &ret = dp[idx][in][de];
        if (~ret) return ret;
        int cur = 0;
        for (int i = in; i <= a[idx]; ++i) {
            int j = a[idx] - i;
            if (j <= de) {
                cur += fun(idx + 1, i, j, a);
                if (cur >= Mod) cur -= Mod;
            }
        }
        return ret = cur;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return fun(0, 0, 50, nums);
    }
};
