int dp[2001][52][52];
int ans=0,mod=1e9+7;
class Solution {
private:
int solve(int i,int prea,int preb,int n,vector<int> &nums){
    if(i==n) return 1;
    if(dp[i][prea+1][preb+1]!=-1) return dp[i][prea+1][preb+1]%mod;
    int s=max(prea,0),ans=0;
    for(int a=s;a<=nums[i];a++){
        if(prea==-1 && preb==-1){
            int tmp=solve(i+1,a,nums[i]-a,n,nums);
            ans=(ans%mod+tmp%mod)%mod;
        } 
        else if(nums[i]-a<=preb){
            int tmp=solve(i+1,a,nums[i]-a,n,nums);
            ans=(ans%mod+tmp%mod)%mod;
        }
    }
    return dp[i][prea+1][preb+1]=ans%mod;
}
public:
    int countOfPairs(vector<int>& nums) {
        ans=0;
        int a=-1,b=-1,n=nums.size();
        memset(dp,-1,sizeof(dp));
        int ans=solve(0,a,b,n,nums);
        return ans;
    }
};
