import java.util.Scanner;

class Solution {
    public int countOfPairs(int[] arr) {  // Revert the method name back to `countOfPairs`
        final int MODULO = 1000000007;
        int length = arr.length;
        int maxElement = 50;

        // Create a 3D DP table
        int[][][] dpTable = new int[length][maxElement + 1][maxElement + 1];

        // Initialize the DP table for the first element
        for (int x = 0; x <= maxElement; x++) {
            int y = arr[0] - x;
            if (y >= 0 && y <= maxElement) {
                dpTable[0][x][y] = 1;
            }
        }

        // Fill the DP table
        for (int i = 1; i < length; i++) {
            for (int x = 0; x <= maxElement; x++) {
                for (int y = 0; y <= maxElement; y++) {
                    if (dpTable[i - 1][x][y] > 0) {
                        for (int newX = 0; newX <= x; newX++) {
                            int newY = arr[i] - newX;
                            if (newY >= y && newY <= maxElement) {
                                dpTable[i][newX][newY] = (dpTable[i][newX][newY] + dpTable[i - 1][x][y]) % MODULO;
                            }
                        }
                    }
                }
            }
        }

        // Sum up all valid pairs for the last index
        int totalPairs = 0;
        for (int x = 0; x <= maxElement; x++) {
            for (int y = 0; y <= maxElement; y++) {
                totalPairs = (totalPairs + dpTable[length - 1][x][y]) % MODULO;
            }
        }

        return totalPairs;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        Solution solution = new Solution();

        System.out.print("Enter the number of elements: ");
        int length = scanner.nextInt();

        int[] arr = new int[length];
        System.out.print("Enter the elements: ");
        for (int i = 0; i < length; i++) {
            arr[i] = scanner.nextInt();
        }

        System.out.println("The number of monotonic pairs is: " + solution.countOfPairs(arr));
    }
}
