class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mod=1e9+7;
        vector<vector<long long>> dp(n,vector<long long>(1001,0));
        for(int i=0;i<=nums[0];i++)dp[0][i]++;
        for(int i=1;i<n;i++){
            for(int j=1;j<1001;j++)dp[i-1][j]=(dp[i-1][j]+dp[i-1][j-1])%mod;
            for(int num=0;num<=nums[i];num++){
                int cnum=nums[i-1]+num-nums[i];
                int x=min(cnum,num);
                if(x<0)continue;
                dp[i][num]=(dp[i][num]+dp[i-1][x])%mod;
            }
        }
        long long ans=0;
        for(int i=0;i<1001;i++)ans=(ans+dp[n-1][i])%mod;
        return ans;
    }
};
