class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        prev1 = 0
        prev2 = nums[0]
        MOD = 10**9 + 7
        cache = {}
        def recur(idx,prev1,prev2):
            if idx==n:
                return 1
            if (idx,prev1,prev2) in cache:
                return cache[(idx,prev1,prev2)]
            res = 0
            curr = nums[idx]
            for x in range(prev1,curr+1):
                if x>=prev1 and curr-x<=prev2:
                    res =  (res + recur(idx+1,x,curr-x))%MOD
            cache[(idx,prev1,prev2)] = res %MOD
            return cache[(idx,prev1,prev2)]

        res = recur(0,prev1,prev2)
        return res %MOD
