class Solution {
    const int MOD = 1e9 + 7;
    private:


    int countPairs(const vector<int>& nums, int i, int prev,vector<vector<int>>& dp) {
        if (i == nums.size()) return 1;
        if (dp[i][prev] != -1) return dp[i][prev];

        int total = 0;
        for (int x = prev; x <= nums[i]; ++x) {         
            int y = nums[i] - x;
            if (i == 0 || y <= nums[i - 1] - prev) {
                total = (total + countPairs(nums, i + 1, x,dp)) % MOD;
            }
        }

        return dp[i][prev] = total;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(2001,vector<int>(2001,-1));
        return countPairs(nums, 0, 0, dp);
    }
};
