       class Solution {
public:
    int r=1e9+7;
    long long solve(int a,int b,int idx,vector<int>&nums, map<pair<pair<int,int>,int>,long long>&dp){
        if(idx==nums.size())
            return 1;

        if(dp.find({{a,b},idx})!=dp.end())
            return dp[{{a,b},idx}]%r;

        long long ans=0;
        for(int i=a;i<=nums[idx];i++){
           int j=nums[idx]-i;
          if(j<=b){
            ans=(ans%r+solve(i,j,idx+1,nums,dp)%r)%r;
          }
               // cout<<j<<" ";
        }
        // cout<<endl;
        return dp[{{a,b},idx}]=ans%r;
    }
    int countOfPairs(vector<int>& nums) {
        map<pair<pair<int,int>,int>,long long>dp;
        return solve(0,nums[0],0,nums,dp)%r;
    }
};
