//here I think at lib. a O(n3) solution is permissible
//or should I say O(n2 log n)
//actually a O(n2 * k) solution is permissible 4000000*50=2 * 10**8

class Solution {
public:
    vector<vector<int>> dp;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),m=50,i,j;
        const int MOD= (int)1e9 + 7;
        dp=vector<vector<int>>(m+1,vector<int>(n+1,0));
        //each row correspond to a value of a[i] where a[i]+b[i]=nums[i]
        //each col corresponds to index i
        //nums[j][n-1]=1 for 0<=a<=nums[i] and 0 otherwise
        //dp[i][j] = count of all monotonic subarrays [j,n-1] with a as i 
        int a,b;
        for(j=n-1,i=0;i<=nums[j];i++){
            dp[i][j]=1;
        }
        for(j=n-2;j>=0;j--){
        for(i=0;i<=nums[j];i++){
            a=i;
            b=nums[j]-a;
            for(int k=a;k<=nums[j+1];k++){
                if (a<=k && b>=nums[j+1]-k){
                    dp[i][j]+=dp[k][j+1];
                    dp[i][j]%=MOD;
                }
            }
        }
        }
        int answer=0;
        for(j=0,i=0;i<=50;i++){
            answer+=dp[i][j];
            answer%=MOD;
        }
        return answer;

    }
};
