class Solution {
public:
    int mod=1e9+7;
    long long helper(int index,vector<int>&nums,int l1,int l2,long long & count, vector<vector<long long>>&dp)
    {
        if(index>=nums.size())
        {
            // cout<<l1<<" " <<l2<<endl;
            // cout<<endl;

            return 1LL;
        }
        if(dp[index][l1]!=-1)return dp[index][l1];
        //cout<<l1<<" " <<l2<<endl;
       int val=nums[index];
       long long sum=0;

        for(int i=l1;i<=nums[index];i++)
        {
            int j=nums[index]-i;
            if(j>l2 )continue;
           sum+= helper(index+1,nums,i,j,count,dp);
        }
        sum%=mod;
        return dp[index][l1]=sum;

    }
    int countOfPairs(vector<int>& nums) {
        int sz=nums.size();
        long long count=0;
        vector<vector<long long>>dp(sz,vector<long long>(51,-1));
    for(int i=0;i<=nums[0];i++)
    {  
       count+= helper(1,nums,i,nums[0]-i,count,dp);
    }
    count%=mod;
    return count;

    }
};
