#define ll long long int

class Solution {
public:
    ll MOD = 1e9 + 7;
    long long f(int ind, int v1, int v2, vector<int>& values, vector<vector<vector<ll>>>& dp) {
        if (ind >= values.size()) return 1;

        ll ans = 0;
        if (dp[ind][v1][v2] != -1) return dp[ind][v1][v2];

        for (int i = v1; i <= values[ind]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j == values[ind]) {
                    ans += f(ind + 1, i, j, values, dp);
                    ans = ans%MOD;
                }
            }
        }
        return dp[ind][v1][v2] = ans;
    }

    int countOfPairs(vector<int>& values) {
        int n = values.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return f(0, 0, values[0], values, dp);
    }
};
