class Solution(object):
    def countOfPairs(self, nums):
        dp,mod={},10**9+7
        def solve(idx,x,y):
            if idx==len(nums):
                return 1
            elif (idx,x,y) in dp:
                return dp[(idx,x,y)]
            else:
                c1=0
                for i in range(nums[idx]+1):
                    if i>=x and nums[idx]-i<=y:
                        c=solve(idx+1,i,nums[idx]-i)
                        c1=c1+c
                dp[(idx,x,y)]=c1
                return c1
        return solve(0,0,nums[0])%mod
