class Solution {
    int MOD = 1_000_000_007;
    Integer[][][] memo;
    public int countOfPairs(int[] nums) {
        // memo = new int[nums.length][]
        memo = new Integer[nums.length][51][51];
        return helper(0, nums, 0, nums[0]);
    }
    private int helper(int index, int[] nums, int prev1, int prev2) {
        if(index == nums.length) {
            return 1;
        }
        // System.out.println(index + " " + prev1 + " " + prev2);
        if(memo[index][prev1][prev2] != null) {
            return memo[index][prev1][prev2];
        }
        int sum = nums[index];
        int res = 0;
        for(int i = 0; i <= sum; i++) {
            int num1 = i;
            int num2 = sum - i;
            if(num1 >= prev1 && num2 <= prev2) {
                int nextRes = helper(index + 1, nums, num1, num2);
                res = (res + nextRes) % MOD;
            }

        }
        memo[index][prev1][prev2] = res;
        return res;
    }
}
