#include <vector>
#include <cstring>

class Solution {
public:
    int countOfPairs(std::vector<int>& nums) {
        const int MOD = 1e9 + 7;
        const int MAX_VAL = 50;  

        int n = nums.size();
        std::vector<std::vector<std::vector<int>>> dp(n + 1, std::vector<std::vector<int>>(MAX_VAL + 1, std::vector<int>(MAX_VAL + 1, -1)));

        std::function<int(int, int, int)> solve = [&](int index, int minArr1, int maxArr2) -> int {
            if (index == n) {
                return 1; 
            }
            if (dp[index][minArr1][maxArr2] != -1) {
                return dp[index][minArr1][maxArr2];  
            }

            int count = 0;
            for (int i = minArr1; i <= nums[index]; ++i) {
                int arr2Value = nums[index] - i;
                if (arr2Value <= maxArr2) {
                    count = (count + solve(index + 1, i, arr2Value)) % MOD;
                }
            }

            dp[index][minArr1][maxArr2] = count;  
            return count;
        };

        return solve(0, 0, 50);  
    }
};
