class Solution {
public:
    const int mod = 1e9+7;
    int rec(int ind,int i,int j,vector<int>&v,int n,vector<vector<vector<int>>>&dp){
        if(ind==n-1){
            return 1;
        }
        if(dp[ind][i][j]!=-1){return dp[ind][i][j];}
        int sum=0;
        for(int ii=0;ii<=v[ind+1];ii++){
            if(ii >= i && v[ind+1]-ii <= j){
                sum = (sum + rec(ind +1, ii,v[ind+1]-ii,v,n,dp)) % mod  ;
            }
        }
        return dp[ind][i][j] = sum% mod ;
    }
    int countOfPairs(vector<int>& v) {
        int n=v.size();
        int sum=0;
        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(55,vector<int>(55,-1)));
        for(int i=0;i<=v[0];i++){
            sum = (sum + rec(0,i,v[0]-i,v,n,dp)) % mod;
        }
        return sum %mod;
    }
};
