class Solution {
    using ll = long long;
    ll mod = 1e9 + 7;
    ll solve(vector<int>& nums, vector<vector<vector<ll>>>& dp, int pos,
              int num1, int num2) {
        int n = nums.size();
        if (pos == n) {
            return 1;
        }
        if(dp[pos][num1][num2]!=-1){
            return dp[pos][num1][num2];
        }
        int currnum = nums[pos];
        ll ans = 0;
        if (pos == 0) {
            for (int i = 0; i <= currnum; i++) {
                ans += (ll)solve(nums, dp, pos + 1, i, currnum - i) % mod;
            }
        } else {
            for (int i = 0; i <= currnum; i++) {
                if (num1 <= i && num2 >= (currnum - i))
                    ans += (ll)solve(nums, dp, pos + 1, i, currnum - i) % mod;
            }
        }
        return dp[pos][num1][num2]= (ll)ans % mod;
    }

public:
    int countOfPairs(vector<int>& nums) { 
        int n = nums.size();
        vector<vector<vector<ll>>> dp(
            n, vector<vector<ll>>(52, vector<ll>(52, -1ll)));
        ll ans = solve(nums, dp, 0, 0, 51) % mod;
        return (int) ans;
        // return ans;
    }
};
