class Solution {
public:

  long long int rs(int i, int a, int b, const vector<int>& vals, vector<vector<vector<long long int>>>& dp) {
        if (i >= vals.size())
        return 1;
        long long int res = 0;
        long long int mod = 1e9 + 7;

        if (dp[i][a][b] != -1)
        return dp[i][a][b];

        for (int x = a; x <= vals[i]; x++) {

            for (int y = b; y >= 0; y--) {
            if (x + y == vals[i]) {
                     res += rs(i + 1, x, y, vals, dp);
                    res %= mod;
                 }
          }
            }
        return dp[i][a][b] = res;
    }


    int countOfPairs(vector<int>& vals) {
        int n = vals.size();
        vector<vector<vector<long long int>>> dp(n, vector<vector<long long int>>(51, vector<long long int>(51, -1)));

        return rs(0, 0, vals[0], vals, dp);
    }


};
