class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        @cache
        def dfs(cur, pre_a1, pre_a2):
            if cur == n:
                return 1

            res = 0
            for cur_a1 in range(pre_a1, nums[cur] + 1):
                cur_a2 = nums[cur] - cur_a1
                if cur > 0 and cur_a2 > pre_a2:
                    continue
                res = (res + dfs(cur + 1, cur_a1, cur_a2)) % MOD

            return res

        res = 0
        for a1 in range(nums[0] + 1):
            a2 = nums[0] - a1
            # from the second item
            res += dfs(1, a1, a2) % MOD

        return res % MOD
