class Solution {
public:

    const int MOD = 1e9 + 7;

	int countWays(vector<int> &nums, int idx, int prevVal1, int prevVal2, vector<vector<vector<int>>> &dp) {
		int n = nums.size();
		if (idx == n) return 1;
		if (dp[idx][prevVal1][prevVal2] != -1) return dp[idx][prevVal1][prevVal2];
		//prevVal1 <= val1
		//prevVal2 >= val2
		int ways = 0;
		for (int val1 = prevVal1; nums[idx] - val1 >= 0; val1++) {
			int val2 = nums[idx] - val1;
			if (prevVal2 >= val2) {
				ways = (ways + countWays(nums, idx + 1, val1, val2, dp)) % MOD;
			}
		}

		return dp[idx][prevVal1][prevVal2] = ways;
	}


    int countOfPairs(vector<int>& nums) {
		int n = nums.size();
		vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
		return countWays(nums, 0, 0, 50, dp);
    }
};
