class Solution {
public:
     const int MOD = 1e9 + 7;






    int f( vector<int>& nums, int ind, int prev1, int prev2,vector<vector<vector<int>>> &dp) {
        if (ind >= nums.size())  return 1;

        if (dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2];

        int ans = 0;
        for (int i = 0; i <= nums[ind]; ++i) {
            int k1 = i;
            int k2 = nums[ind] - i;
            if (k1 >= prev1 && k2 <= prev2) {
                ans = (ans + f(nums, ind + 1, k1, k2,dp)) % MOD;
            }
        }


        return dp[ind][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(nums, 0, 0, 50,dp);
    }
};
