int dp[2001][51][51];
class Solution {
public:
int m;
int solve(int i,int prev1,int prev2,vector<int>&nums){
    if(i==nums.size())return 1;
    int cnt=0;
    if(dp[i][prev1][prev2]!=-1)return dp[i][prev1][prev2];
    for(int k=prev1;k<=50;k++){
        for(int j=prev2;j>=0;j--){
            if(k+j==nums[i]){
                cnt=((cnt%m)+solve(i+1,k,j,nums)%m)%m;
            }
        }
    }
    return dp[i][prev1][prev2]=cnt;
}
    int countOfPairs(vector<int>& nums) {
        m=1e9+7;
        memset(dp,-1,sizeof(dp));
        return solve(0,0,50,nums);
    }
};
