class Solution {
public:
    int mod = 1000000007;
    long long solve(int idx, int val1, int val2, vector<int>&nums, vector<vector<vector<long long>>> &dp){
        int n = nums.size();
        if(idx == n) return 1;
        if(dp[idx][val1][val2]!=-1) return dp[idx][val1][val2];

        int total = 0;
        for(int i=val1; i<=nums[idx]; i++){
            for(int j=nums[idx]; j>=0; j--){
                if(j>val2) continue;
                if(i+j == nums[idx]) total += solve(idx+1, i, j, nums, dp);
                total = total%mod;
            }
        }
        return dp[idx][val1][val2] = total%mod;

    }
