class Solution {
public:
    int mod = 1e9+7;
    int f(int ind, int a, int b, vector<vector<vector<int>>>& dp, vector<int>& nums){
        if(ind>=nums.size()) return 1;
        int ans = 0;
        if(dp[ind][a][b]!=-1) return dp[ind][a][b];
        for(int i=a;i<=nums[ind];i++){
            for(int j=b;j>=0;j--){
                if(i+j == nums[ind]){
                    ans += f(ind+1, i, j, dp, nums);
                    ans = ans%mod;
                }
            }
        }
        return dp[ind][a][b] = ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n= nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>> (51, vector<int> (51, -1)));
        return f(0,0, nums[0], dp, nums);

    }
};
