class Solution {
public:
    const int MOD = 1000000007;
    vector<vector<vector<int>>> dp;

    int f(const std::vector<int>& nums, int i, int p1, int p2) {
        if (i >= nums.size()) {
            return 1;
        }

        if (dp[i][p1][p2] != -1) {
            return dp[i][p1][p2];
        }

        int res = 0;
        for (int x = 0; x <= nums[i]; ++x) {
            int y = nums[i] - x;
            if (x >= p1 && y <= p2) {
                res = (res + f(nums, i + 1, x, y)) % MOD;
            }
        }

        dp[i][p1][p2] = res;
        return res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(nums, 0, 0, 50);
    }
};
