class Solution {
public:
    int mod=1e9+7;
    int f(vector<vector<int>>&dp,int i,int p1,int p2,vector<int>&nums){
        if(i==nums.size()){
            if(p1+p2<=nums[i-1]){
                return 1;
            }
            return 0;
        }
        if(dp[i][p1]!=-1){
            return dp[i][p1];
        }
        int ans=0;
        for(int i1=p1;i1<=nums[i];i1++){
            if(nums[i]-i1<=p2){
                ans+=f(dp,i+1,i1,nums[i]-i1,nums);
                ans%=mod;
            }
        }
        return dp[i][p1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(51,-1));
        return f(dp,0,0,50,nums);
        // return dp[n-1];
    }
};
