class Solution {
public:
    int m = 1e9+7;
    vector<unordered_map<int,unordered_map<int,int>>> dp;
    int countOfPairs(vector<int>& nums) {
        dp.resize(2001);

        return solve(nums, 0, 0, 51);
    }
    int solve(vector<int> &nums, int i, int l1, int l2){
        if(i == nums.size()) return 1;
        if(dp[i].find(l1) != dp[i].end() && dp[i][l1].find(l2) != dp[i][l1].end()) return dp[i][l1][l2];

        int res = 0;
        for(int a1=l1; a1<=nums[i]; a1++){
            int a2 = nums[i]-a1;
            if(l2 >= a2){
                int tmp = solve(nums, i+1, a1, a2);
                res = (tmp + res)%m;
            }
        }

        return dp[i][l1][l2] = res;
    }
};
