class Solution {
    public int countOfPairs(int[] nums) {
        int s=nums.length;
        long[][][] dpp=new long[s][51][51];
        for(long[][] m:dpp){
            for(long[]m1:m){
                Arrays.fill(m1,-1);
            }
        }
        return (int) fun(nums,0,0,nums[0],dpp);
    }
    public long fun(int[] nums,int indexx,int val11,int val22,long[][][]dpp){
        if(indexx>=nums.length)return 1;
        long res=0;
        long MD=1000000007;
        if(dpp[indexx][val11][val22] != -1)return dpp[indexx][val11][val22];
        for(int i=val11;i<=nums[indexx];i++){
            for(int j=val22;j>=0;j--){
                if(i+j==nums[indexx]){
                    res += fun(nums,indexx + 1,i,j,dpp);
                    res %= MD;
                }
            }
        }
        return dpp[indexx][val11][val22] = res;
    }
}
